import React, { Component } from 'react'
import Emoji from './Emoji'

export default class Tweets extends Component {
    //t.sentiment.score
    getEmoji = (sentimentScore) => {
        emoji = "🐑";


        return emoji;
    }

    render() {
        return (
            <div>
                <ul>
               {
                   this.props.apiResponse.map((t) => {
                       return (
                       <div key={t.id}>
                           <li>
                            <p>{t.text}</p>
                            <Emoji symbol={this.getEmoji(t.sentiment.score)}/>
                           </li>
                       </div>)
                   })
               }
               </ul>
            </div>
        )
    }
}
